
public class OperatorEx3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 3;
		int b = 2;
		int c = 5;
		
		System.out.println((a-b)*c);
		
		System.out.println(a > 2 && b > 2);
		
		System.out.println(a > 2 || b > 2);

		System.out.println(++a - 3);
		System.out.println(a++ - 3);

		System.out.println(a);
	}

}
