
public class DataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean isFun = false;  //true, false중 하나의 값만 가짐
		System.out.println(isFun);
		
		char c = 'a'; //문자 단 하나만 표현 가능
		
		int x =10;
		
		long y = 100000000l; //long 타입을 선언하기 위해서는 뒤에 l, L 을 넣어야함
		
		float f = 21.4f; // f 를 넣어야함
		
		double d = 191909.123;
		
		
		
	}

}
